/* Custom Front-End jQuery scripts */

jQuery(function() {
	jQuery('#push-menu').mmenu();
});
			
jQuery(document).ready(function() {
	jQuery('.mobile-menu').show();
	
	jQuery('.header-search-toggle').click(function(){
		jQuery('.search-box').addClass('active');
		return false;		
	});
	
	jQuery('a:not([href="#"])').attr('target','_self');
	jQuery('.price-menu a').removeAttr('target');
	
	jQuery('.header-menu li').has('> ul').mouseenter(function(){
		jQuery(this).stop().addClass('hover');
		jQuery('.menu-overlay').stop().addClass('active');
	});
	
	jQuery('.header-menu li').has('> ul').mouseleave(function(){
		jQuery(this).stop().removeClass('hover');
		jQuery('.menu-overlay').stop().removeClass('active');
	});
	
	jQuery('.search-form').click(function(event){
		event.stopPropagation();
	})
	
	jQuery('.search-box').click( function(){
        jQuery('.search-box').removeClass('active');
    });
	
	if(jQuery('.search-box').is(':visible')){
	jQuery(document).keyup(function(e) {
		if (e.keyCode == 27) {
			jQuery('.search-box').removeClass('active');
		}
	});
	}
	
	jQuery('.search-close-button').click( function(){
        jQuery('.search-box').removeClass('active');
    });
	
	jQuery("#suggested").owlCarousel({ 
		nav : false,
		dots:false,
		margin : 23,
		loop : true,
		rtl : true,
		autoplay:true,
		stagePadding: 10,
		responsive : {
			0 : {
				items : 1,
			},
			500 : {
				items : 2,
			},
			670 : {
				items : 3,
			},
			800 : {
				items : 4,
			},
			980 : {
				items : 5,
			},
			1220 : {
				items : 6,
			}
		}
	});
	
	var active_tab_link = jQuery(".home-tab-link li.active").data('tab');
	jQuery('#'+active_tab_link).show();
	
	jQuery(".home-tab-link li").click(function(){
		jQuery(".home-tab-link li").removeClass('active');
		jQuery(this).addClass('active');	
		var active_tab = jQuery(this).data('tab');
		jQuery('.home-tab-content-box').hide();
		jQuery('#'+active_tab).stop().fadeIn();
		jQuery('html, body').stop().animate({
			scrollTop: jQuery( '.home-tab-content' ).offset().top-20-70
		}, 500);
		
		var category_id = jQuery(this).data('tab').split('-');
		
		// Ajax call
		jQuery.ajax({
			type: "post",
			url: ajax_url.ajax_url,
			data: "action=get_category_post&cat_id="+category_id[1],
			success: function( data ){
				jQuery( '#category-post-wrapper-'+category_id[1] ).html( data );                                                    
			},
			error: function( jqXHR, textStatus, errorThrown ){
				console.log( 'The following error occured: ' + textStatus, errorThrown );   
			},
			complete: function( jqXHR, textStatus ){
				
			}
		});
	});
	
	// Default Link Parameters
	var cat = jQuery(".price-submenu li.active a").data('cat');
	var limit = jQuery(".price-submenu li.active a").data('limit');
	var brand = jQuery(".price-submenu li.active a").data('brand');
	
	jQuery.ajax({
		type: "post",
		url: ajax_url.ajax_url,
		data: "action=get_price&cat="+cat+"&limit="+limit+"&brand="+brand,
		success: function( data ){
			jQuery( '#ajax-price' ).html( data );                                                    
		},
		error: function( jqXHR, textStatus, errorThrown ){
			console.log( 'The following error occured: ' + textStatus, errorThrown );   
		},
		complete: function( jqXHR, textStatus ){
			jQuery( '.price-main-content-loading' ).hide();
		}
	});
	
	jQuery(".price-submenu li a").click(function(){
		
		jQuery('.price-submenu li').removeClass('active');
		jQuery(this).parent().addClass('active');
		
		jQuery( '.price-main-content-loading' ).show();
		jQuery( '#ajax-price' ).empty();
		
		// Link Parameters
		var cat = jQuery(this).data('cat');
		var limit = jQuery(this).data('limit');
		var brand = jQuery(this).data('brand');
		
		jQuery.ajax({
			type: "post",
			url: ajax_url.ajax_url,
			data: "action=get_price&cat="+cat+"&limit="+limit+"&brand="+brand,
			success: function( data ){
				jQuery( '#ajax-price' ).html( data );                                                    
			},
			error: function( jqXHR, textStatus, errorThrown ){
				console.log( 'The following error occured: ' + textStatus, errorThrown );   
			},
			complete: function( jqXHR, textStatus ){
				jQuery( '.price-main-content-loading' ).hide();
			}
		});
		return false;
	});
	
	jQuery(".home-min-link").stick_in_parent({
		offset_top: 70,
		recalc_every:1
	});
	
	jQuery(".sidebar").stick_in_parent({
		offset_top: 70,
	});

	
	jQuery(".comment-toggle").click(function(){
		jQuery(".comments-add-box").toggle();
		if(jQuery('.comments-add-box').is(":visible") == true) { 
			jQuery(this).html('بستن'); 
		} else {
			jQuery(this).html('');
		}		
	})
});

jQuery(function() {	
	var header = jQuery('.header').offset().top;
	jQuery(window).scroll(function (event) {
		var y = jQuery(this).scrollTop();
		if (y >= header){
			jQuery('.header').addClass('fixed-header');
		} else {
			jQuery('.header').removeClass('fixed-header');
		}
	});
});


jQuery(document).ready(function() {





    $(".navicon").click(function() {
        $(".bodydeactive").fadeIn(400), $(".menu").animate({
            marginRight: "0px"
        }, 200, "linear")
    });
    $(".navicon2,.bodydeactive").click(function() {
        $(".menu").animate({
            marginRight: "0px"
        }, 200, "linear"), $(".bodydeactive").fadeOut(400)
    });
    $('a.poplight[href^=#]').click(function() {
        var popID = $(this).attr('rel');
        var popURL = $(this).attr('href');
        var query = popURL.split('?');
        var dim = query[1].split('&');
        var popWidth = dim[0].split('=')[1];
        $('#' + popID).fadeIn().prepend('<div class="close"></div>');
        var popMargTop = ($('#' + popID).height() + 80) / 2;
        var popMargLeft = ($('#' + popID).width() + 80) / 2;
        $('#' + popID).css({
            'margin-top': -popMargTop,
            'margin-left': -popMargLeft
        });
        $('body').append('<div id="fade"></div>');
        $('#fade').css({
            'filter': 'alpha(opacity=80)'
        }).fadeIn();
        return false;
    });
    $('.close, #fade').live('click', function() {
        $('#fade , .popup').fadeOut(function() {
            $('#fade, .close').remove();
        });
        return false;
    });
    $('.text .body,.jarchives,.post .screenshot,.post .tag h3').enscroll({
        verticalScrollerSide: "left",
        showOnHover: true,
        verticalTrackClass: 'track',
        verticalHandleClass: 'handle'
    });
    window.setInterval(function() {
        $('.download .links .des small,.notification i').toggleClass('blink');
    }, 1000);
    $(".ntc").click(function() {
        $(".ntc i").fadeOut(300);
        $(".bodydeactive2").fadeIn(100);
        $(".notification .news").fadeIn(100).animate({
            marginTop: "25px"
        }, 10);
    });
    $(".bodydeactive2,.news .title .close").click(function() {
        $(".bodydeactive2").fadeOut(300);
        $(".notification .news").fadeOut(300).animate({
            marginTop: "10px"
        }, 0);
        $(".ntc i").fadeIn(350);
    });
    setTimeout(function() {
        $(".alert,.alert2,.alert3").fadeOut(2500)
    })
});









